import React from 'react';
import "./Login.css"

export default class Login extends React.Component{
    constructor() {
        super();
        this.state = {
            click: false,
        }
    }

    onSubmit = (e) => {
        // alert("登录成功")
        e.preventDefault();
    }

    onClick = (e) => {
        this.setState({click: true}, () => {
            this.setState({click: false});
        });
    }
    render() {
        return(
            <div className="login-container">
                <div className="login-title">
                    <p>登录/注册</p>
                </div>
                <form className="login-form-container" onSubmit={this.onSubmit}>
                    <div className="login-form-input-container">
                        <label className="login-form-input-item login-form-input-account">
                            <svg className="icon svg-category" aria-hidden="true">
                                <use xlinkHref="#icon-category" />
                            </svg>
                            <input className="login-form-input" required type="number" placeholder="请输入账号" minLength={6} maxLength={16}/>
                        </label>
                        <label className="login-form-input-item">
                            <svg className="icon svg-category" aria-hidden="true">
                                <use xlinkHref="#icon-category" />
                            </svg>
                            <input className="login-form-input" required type="password" placeholder="请输入密码" maxLength={20}/>
                        </label>
                    </div>
                    <button className={this.state.click ? "login-form-btn-click" : "login-form-btn"} type="submit" onClick={this.onClick}>登录/注册</button>
                </form>
            </div>
        )
    }
}
