import React from 'react'
import ReactECharts from 'echarts-for-react';
import { DatePicker } from 'antd-mobile';
import 'antd-mobile/dist/antd-mobile.css';
import {get} from "../../utils/request";
import history from "../../utils/history";
import "./Graph.css"

let option = {
    xAxis: {
        type: 'category',
        data: ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun']
    },
    yAxis: {
        type: 'value'
    },
    series: [{
        data: [150, 230, 224, 218, 135, 147, 2600],
        type: 'line'
    }]
};

export default class Graph extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            type: "1",
            date: new Date(),
            dpValue: null,
            customChildValue: null,
            visible: false,
        }
    }

    componentDidMount() {
        const data = {
            month: this.state.date.Format("yyyy-MM")
        }
        console.log(data);
        get('getGraphDate', data).then((res) => {
            console.log(res.data.result.outDetail);
            this.setState({
                outDetail: res.data.result.outDetail,
                inDetail: res.data.result.inDetail,
            })
        })
    }
    changeType = (e) => {
        this.setState({[e.target.name]: e.target.value});
    }
    changeDate = (date) => {
        this.setState({date: date});
        this.componentDidMount();
    }
    render() {
        const option = {
            tooltip: {
                trigger: "item"
            },
            series: [
                {
                    name: this.state.type === "1" ? "支出" : "收入",
                    type: "pie",
                    radius: "50%",
                    data: this.state.type === "1" ? this.state.outDetail : this.state.inDetail
                }
            ]
        };
        return(
            <div>
                <select name="type" className="select-item" onChange={this.changeType}>
                    <option value="1">支出</option>
                    <option value="0">收入</option>
                </select>
                <DatePicker
                    mode="month"
                    value={this.state.date}
                    onChange={this.changeDate}
                >
                    <div>选着日期</div>
                </DatePicker>
                <ReactECharts option={option} />
            </div>
        )
    }
}
