import React from 'react';
import {Link} from 'react-router-dom'
import './BillList.css';

let sum = {
    time: "2021.6",
    out: 1000,
    in : 2000000,
}
let billList = [
    {
        date: "2021.6.5",
        nums: 3,
        list: [
            {
                category: "food",
                type: 0,
                amount: 100,
                remark: "火锅1"
            },
            {
                category: "food",
                type: 0,
                amount: 100,
                remark: "火锅2"
            },
            {
                category: "food",
                type: 0,
                amount: 100,
                remark: "火锅3"
            },
        ]
    },
    {
        date: "2021.6.8",
        nums: 2,
        list: [
            {
                category: "food",
                type: 0,
                amount: 100,
                remark: "火锅1"
            },
            {
                category: "food",
                type: 1,
                amount: 1000,
                remark: "火锅2"
            }
        ]
    },
    {
        date: "2021.6.15",
        nums: 1,
        list: [
            {
                category: "food",
                type: 0,
                amount: 100,
                remark: "火锅1"
            }
        ]
    }
]

 class TabBottom extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            tabList:props.tabList,
            activeKey:props.defaultTab||0
        }
    }
    changeTab(item){
        this.props.history.push(item.path);
        const {onClick} =this.props;
        if (onClick) {
            onClick(item);
        }
    }
    render() {
        const {prefixCls} = this.props;
        return (
            <div>
                <div className={prefixCls}>
                    {this.state.tabList.map((item,index)=>{
                        return(<div key={index} className={index == this.state.activeKey?prefixCls+"-tab-active":prefixCls+"-tab"} onClick={()=>this.changeTab(item)}>
                            <img className={prefixCls+"-icon"} src={index == this.state.activeKey?item.iconAct:item.icon} alt=""/>
                            <span className={prefixCls+"-span"}>{item.label}</span>
                        </div>)
                    })}
                </div>
            </div>
        )
    }
}

export default class BillList extends React.Component{
    constructor(props) {
        super(props);
    }

    componentDidMount() {
    }

    render() {
        const listItem = billList.map((oneDay, index) => {
            return (
                <div className="list-container" key={index}>
                    <div className="list-date">
                        <p>{oneDay.date}</p>
                    </div>
                    <ul>
                        {oneDay.list.map((item, index) => {
                            return (
                                <div className="list-item" key={index}>
                                    <div className="list-item-info">
                                        <p>{item.category}</p>
                                        <p>{item.remark}</p>
                                    </div>
                                    <div className="list-item-amount">
                                        <p><span>{item.type === 0 ? "+" : "-"}</span>{item.amount}</p>
                                    </div>
                                </div>
                            )
                        })}
                    </ul>
                </div>
            )
        });
        return(
            <div className="container">
                <div className="info-title">
                    <p>我的账单</p>
                </div>
                <div className="info-sum">
                    <div className="info-sum-time">
                        <p>
                            {sum.time}
                        </p>
                    </div>
                    <div className="info-sum-amount">
                        <p style={{marginRight:"10px"}}>
                           <span>总支出&yen;</span> {sum.out}
                        </p>
                        <p style={{marginRight:"10px"}}>
                            <span>总收入&yen;</span> {sum.in}
                        </p>
                    </div>
                </div>
                <div className="info-list">
                    <ul>{listItem}</ul>
                </div>
                <div className="add">
                    <Link to="/add">记一笔</Link>
                </div>
            </div>
        )
    }
}
