import React from 'react'
import {post} from '../../utils/request.js'
import history from '../../utils/history'
import './AddOne.css'

export default class AddOne extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            type: 1,
            amount: 0,
            category: "food",
            remark: "",
        }
    }

    componentDidMount() {
    }
    back(){
        history.goBack()
    }
    change = e =>{
        this.setState({[e.target.name]: e.target.value});
    }
    changeNum = e =>{
        let value = parseFloat(e.target.value);
        value = ((value.match(/^\d*(\.?\d{0,2})/g)[0]) || null);
        this.setState({[e.target.name]: value});
    }
    changeType = e =>{
        this.setState({[e.target.name]: parseInt(e.target.value)});
    }
    save = () =>{
        let data = {
            amount: this.state.amount,
            category: this.state.category,
            type: this.state.type,
            date: new Date(this.state.date),
            remark: this.state.remark,
        }
        console.log(this.state)
        // post('insertToBill', data).then( (res) => {
        //     console.log(res);
        // })
    }

    render() {
        return(
            <div className="container">
                <div className="add-title">
                    <button className="back-btn" onClick={this.back}>
                        <svg className="icon svg-back" aria-hidden="true">
                            <use xlinkHref="#icon-back" />
                        </svg>
                    </button>
                    <div>记一笔</div>
                </div>
                <div className="add-detail">
                    <div className="add-detail-item">
                        <div className="add-detail-item-label">
                            <svg className="icon svg-category" aria-hidden="true">
                                <use xlinkHref="#icon-category" />
                            </svg>
                            <span>
                                <select name="type" className="select-item" onChange={this.changeType}>
                                    <option value="1">支出</option>
                                    <option value="0">收入</option>
                                </select>
                            </span>
                        </div>
                        <input name="amount" className="input-amount" type="number" onChange={this.changeNum}/>
                    </div>
                    <div className="add-detail-item">
                        <div className="add-detail-item-label">
                            <svg className="icon svg-category" aria-hidden="true">
                                <use xlinkHref="#icon-category" />
                            </svg>
                            <span>类别</span>
                        </div>
                        <select name="category" className="select-item" onChange={this.change}>
                            <option value="food">食物</option>
                            <option value="cloth">服装</option>
                            <option value="other">其他</option>
                        </select>
                    </div>
                    <div className="add-detail-item">
                        <div className="add-detail-item-label">
                            <svg className="icon svg-date" aria-hidden="true">
                                <use xlinkHref="#icon-date" />
                            </svg>
                            <span>时间</span>
                        </div>
                        <input name="date" type="date" onChange={this.change}/>
                    </div>
                    <div className="add-detail-item">
                        <div className="add-detail-item-label">
                            <svg className="icon svg-remark" aria-hidden="true">
                                <use xlinkHref="#icon-remark" />
                            </svg>
                            <span>备注</span>
                        </div>
                        <textarea rows={3} name="remark" onChange={this.change}/>
                    </div>
                    <div>
                        <button onClick={this.save}>保存</button>
                    </div>
                </div>

            </div>
        )
    }
}
