import React from 'react'
import {post} from '../../utils/request.js'
import history from '../../utils/history'
import './AddOne.css'
import {DatePicker} from "antd-mobile";

export default class AddOne extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            type: 1,
            amount: 0.00,
            date: new Date(),
            category: "food",
            remark: "",
            account: this.props.location.state.account,
        }
    }

    componentDidMount() {
        if(this.props.location.state === undefined){
            history.replace("login");
        }
    }
    back(){
        history.goBack()
    }
    change = e =>{
        this.setState({[e.target.name]: e.target.value});
    }
    changeNum = e =>{
        const reg = /^(-)*(\d+)\.(\d\d).*$/;
        e.target.value = e.target.value.replace(reg, "$1$2.$3");
        this.setState({[e.target.name]: parseFloat(e.target.value)});
    }
    changeType = e =>{
        this.setState({[e.target.name]: parseInt(e.target.value)});
    }
    save = () =>{
        let data = {
            account: this.state.account,
            amount: this.state.amount,
            category: this.state.category,
            type: this.state.type,
            date: this.state.date,
            remark: this.state.remark,
        }
        console.log(data);
        post('insertToBill', data).then( (res) => {
            console.log(res);
        })
    }

    render() {
        return(
            <div className="add-one-container">
                <div className="add-title">
                    <button className="back-btn" onClick={this.back}>
                        <svg className="icon svg-back" aria-hidden="true">
                            <use xlinkHref="#icon-back" />
                        </svg>
                    </button>
                    <div>记一笔</div>
                </div>
                <form className="add-detail">
                    <div className="add-detail-item">
                        <div className="add-detail-item-label">
                            <svg className="icon svg-money" aria-hidden="true">
                                <use xlinkHref="#icon-money" />
                            </svg>
                            <span>
                                <select name="type" className="select-item" onChange={this.changeType}>
                                    <option value="1">支出</option>
                                    <option value="0">收入</option>
                                </select>
                            </span>
                        </div>
                        <div className="add-detail-item-input">
                            <input name="amount" className={this.state.type === 1 ? "add-input-out" : "add-input-in"} required value={this.state.amount}
                                   onChange={this.changeNum} placeholder="0.00"/>
                        </div>
                    </div>
                    <div className="add-detail-item">
                        <div className="add-detail-item-label">
                            <svg className="icon svg-category" aria-hidden="true">
                                <use xlinkHref="#icon-category" />
                            </svg>
                            <span>类别</span>
                        </div>
                        <div className="add-detail-item-input">
                            <select name="category" className="select-item add-select" onChange={this.change}>
                                <option value="food">食物</option>
                                <option value="cloth">服装</option>
                                <option value="other">其他</option>
                            </select>
                        </div>
                    </div>
                    <div className="add-detail-item">
                        <div className="add-detail-item-label">
                            <svg className="icon svg-date" aria-hidden="true">
                                <use xlinkHref="#icon-date" />
                            </svg>
                            <span>时间</span>
                        </div>
                        <div className="add-detail-item-input">
                            <DatePicker
                                name="date"
                                mode="date"
                                value={this.state.date}
                                onChange={this.change}
                            >
                                <div>{this.state.date.getFullYear()}-{this.state.date.getMonth() + 1}-{this.state.date.getDate()}</div>
                            </DatePicker>
                        </div>
                    </div>
                    <div className="add-detail-item">
                        <div className="add-detail-item-label">
                            <svg className="icon svg-remark" aria-hidden="true">
                                <use xlinkHref="#icon-remark" />
                            </svg>
                            <span>备注</span>
                        </div>
                        <div className="add-detail-item-input">
                            <input className="add-input" name="remark" onChange={this.change} placeholder="···"/>
                        </div>
                    </div>
                    <div className="add-detail-save">
                        <button className="add-detail-btn" type="submit" onClick={this.save}>保存</button>
                    </div>
                </form>

            </div>
        )
    }
}
