import React from 'react'
import ReactECharts from 'echarts-for-react';
import {DatePicker} from 'antd-mobile';
import 'antd-mobile/dist/antd-mobile.css';
import {get} from "../../utils/request";
import history from "../../utils/history";
import "./Graph.css"

export default class Graph extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            type: "1",
            date: new Date(),
            graph: "pie",
            period: "month",
        }
    }

    componentDidMount() {
        const data = {
            account: this.props.account,
            year: this.state.date.getFullYear(),
            month: this.state.date.getMonth() + 1,
        }
        console.log(data);
        get('getGraphDate', data).then((res) => {
            this.setState({
                sumIn: res.data.result.sumIn,
                sumOut: res.data.result.sumOut,
                outDetail: res.data.result.outDetail,
                inDetail: res.data.result.inDetail,
                inList: res.data.result.inList,
                outList: res.data.result.outList,
            })
        }).catch((err) => {
            alert(err);
        })
    }

    changeType = (e) => {
        this.setState({[e.target.name]: e.target.value});
    }
    changePeriod = (e) => {
        let value = e.target.value;
        let newDate = this.state.from === undefined ? new Date() : new Date(this.state.from);
        if(value === "year"){

        }else if(value === "week"){

        }else{
            let nextMonth = newDate.getMonth() + 1;
            let nextMonthFirstDay = new Date(newDate.getFullYear(), nextMonth, 1);
            let oneDay = 1000 * 60 * 60 * 24;
            let from = new Date(newDate.getFullYear(), newDate.getMonth(), 1);
            let to = new Date(nextMonthFirstDay - oneDay);
            this.setState({
                from: from.format("yyyy.MM.dd"),
                to: to.format("yyyy.MM.dd")
            })
            console.log(from.format("MM.dd"), to, newDate);
        }
    }
    goLeft = () => {
        let newDate = new Date(this.state.date);
        let nextMonth = new Date(newDate.getFullYear(), newDate.getMonth() - 1, 1);
        this.setState({date: nextMonth}, () => {this.componentDidMount()});
    }
    goRight = () => {
        let newDate = new Date(this.state.date);
        let nextMonth = new Date(newDate.getFullYear(), newDate.getMonth() + 1, 1);
        this.setState({date: nextMonth}, () => {this.componentDidMount()});
    }

    render() {
        const list = this.state.outList === undefined ? [] : (this.state.type === "1" ? this.state.outList : this.state.inList);
        console.log(list);
        const optionPie = {
            tooltip: {
                trigger: "item"
            },
            series: [
                {
                    name: this.state.type === "1" ? "支出" : "收入",
                    type: "pie",
                    radius: "50%",
                    data: this.state.type === "1" ? this.state.outDetail : this.state.inDetail
                }
            ]
        };
        const optionList = {
            xAxis: {
                type: 'category',
                data: list.map((item) => {
                    return item.date;
                })
            },
            yAxis: {
                type: 'value'
            },
            series: [{
                data: list.map((item) => {
                    return item.amount;
                }),
                type: 'line'
            }]
        };
        const showPeriod = () => {
            if (this.state.period === "week") {
                return (
                    <div>
                        22
                    </div>
                );
            } else if (this.state.period === "year") {
                return (
                    <div>
                        11
                    </div>
                );
            } else {
                let newDate = new Date(this.state.date);
                let currentMonth = newDate.getMonth();
                let nextMonth = ++currentMonth;
                let nextMonthFirstDay = new Date(newDate.getFullYear(), nextMonth, 1);
                let oneDay = 1000 * 60 * 60 * 24;
                let lastTime = new Date(nextMonthFirstDay - oneDay);
                let year = lastTime.getFullYear();
                let month = lastTime.getMonth() + 1;
                let day = lastTime.getDate();
                return (
                    <div className="graph-head-period">{year === new Date().getFullYear() ? "本年 " : year + "."}{month}.1~{month}.{day}</div>
                );
            }
        }
        return (
            <div className="graph-container">
                <div className="title">
                    <p>图表分析</p>
                </div>
                <div className="graph-head">
                    <div className="graph-head-item">
                        <select name="type" className="select-item graph-select" onChange={this.changeType}>
                            <option value="1">支出</option>
                            <option value="0">收入</option>
                        </select>
                    </div>
                    <div className="graph-head-date graph-head-item">
                        <div>
                            <button className="svg-btn" onClick={this.goLeft}>
                                <svg className="icon svg-left" aria-hidden="true">
                                    <use xlinkHref="#icon-left"/>
                                </svg>
                            </button>
                        </div>
                        <div className="graph-head-period">{this.state.from}~{this.state.to.format("MM")}</div>
                        {/*{showPeriod()}*/}
                        <div>
                            <button className="svg-btn" onClick={this.goRight}>
                                <svg className="icon svg-right" aria-hidden="true">
                                    <use xlinkHref="#icon-right"/>
                                </svg>
                            </button>
                        </div>
                    </div>
                    <div className="graph-head-item">
                        <select name="period" className="select-item graph-select" onChange={this.changePeriod}>
                            <option value="month">月</option>
                            <option value="year">年</option>
                            <option value="week">周</option>
                        </select>
                    </div>
                    <div className="graph-head-item">
                        <select name="graph" className="select-item graph-select" onChange={this.changeType}>
                            <option value="pie">饼图</option>
                            <option value="line">折线图</option>
                        </select>
                    </div>
                </div>

                <div>总计{this.state.type === "1" ? this.state.sumOut : this.state.sumIn}</div>
                <ReactECharts option={this.state.graph === "pie" ? optionPie : optionList} notMerge={true}/>

            </div>
        )
    }
}
