import React from 'react'
import ReactECharts from 'echarts-for-react';
import {get} from "../../utils/request";
import history from "../../utils/history";
import "./Graph.css"

let option = {
    xAxis: {
        type: 'category',
        data: ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun']
    },
    yAxis: {
        type: 'value'
    },
    series: [{
        data: [150, 230, 224, 218, 135, 147, 2600],
        type: 'line'
    }]
};
let option1 = {
    tooltip: {
        trigger: 'item'
    },
    // legend: {
    //     top: '5%',
    //     left: 'center'
    // },
    series: [
        {
            name: '访问来源',
            type: 'pie',
            radius: '50%',
            avoidLabelOverlap: false,
            // itemStyle: {
            //     borderRadius: 10,
            //     borderColor: '#fff',
            //     borderWidth: 2
            // },
            label: {
                show: false,
                position: 'center'
            },
            // emphasis: {
            //     label: {
            //         show: true,
            //         fontSize: '40',
            //         fontWeight: 'bold'
            //     }
            // },
            labelLine: {
                show: false
            },
            data: [
                { name: '搜索引擎',value: 148},
                { name: '直接访问',value: 735},
                { name: '邮件营销',value: 580},
                { name: '联盟广告',value: 484},
                { name: '视频广告',value: 300}
            ]
        }
    ]
};
export default class Graph extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            type: "1",
        }
    }

    componentDidMount() {
        get('getGraphDate').then((res) => {
            console.log(res.data.result.outDetail);
            this.setState({
                outDetail: res.data.result.outDetail,
                inDetail: res.data.result.inDetail,
            })
        })
    }
    changeType = (e) => {
        this.setState({[e.target.name]: e.target.value})
    }
    render() {
        const option = {
            tooltip: {
                trigger: "item"
            },
            series: [
                {
                    name: this.state.type === "1" ? "支出" : "收入",
                    type: "pie",
                    radius: "50%",
                    data: this.state.type === "1" ? this.state.outDetail : this.state.inDetail
                }
            ]
        };
        return(
            <div>
                <select name="type" className="select-item" onChange={this.changeType}>
                    <option value="1">支出</option>
                    <option value="0">收入</option>
                </select>
                <ReactECharts option={option} />
            </div>
        )
    }
}
