import React from 'react'
import history from "../../utils/history";

export default class BottomTab extends React.Component {
    toBill(){
        history.replace("/")
    }
    toGraph(){
        history.replace("graph")
    }
    render() {
        return(
            <div className="bottom-tab">
                <button className="bottom-tab-btn left-btn" onClick={this.toBill}>账单</button>
                <button className="bottom-tab-btn" onClick={this.toGraph}>图表</button>
            </div>
        )
    }
}
