import React from 'react';
import './BillList.css';
import history from '../../utils/history'

let sum = {
    time: "2021.6",
    out: 1000,
    in : 2000000,
}
let billList = [
    {
        date: "2021.6.5",
        nums: 3,
        list: [
            {
                category: "food",
                type: 0,
                amount: 100,
                remark: "火锅1"
            },
            {
                category: "food",
                type: 0,
                amount: 100,
                remark: "火锅2"
            },
            {
                category: "food",
                type: 0,
                amount: 100,
                remark: "火锅3"
            },
        ]
    },    {
        date: "2021.6.5",
        nums: 3,
        list: [
            {
                category: "food",
                type: 0,
                amount: 100,
                remark: "火锅1"
            },
            {
                category: "food",
                type: 0,
                amount: 100,
                remark: "火锅2"
            },
            {
                category: "food",
                type: 0,
                amount: 100,
                remark: "火锅3"
            },
        ]
    },
    {
        date: "2021.6.8",
        nums: 2,
        list: [
            {
                category: "food",
                type: 0,
                amount: 100,
                remark: "火锅1"
            },
            {
                category: "food",
                type: 1,
                amount: 1000,
                remark: "火锅2"
            }
        ]
    },
    {
        date: "2021.6.15",
        nums: 1,
        list: [
            {
                category: "food",
                type: 0,
                amount: 100,
                remark: "火锅1"
            }
        ]
    }
]

export class BottomTab extends React.Component {
    constructor() {
        super();
    }
    toBill(){
        history.replace("/")
    }
    toGraph(){
        history.replace("graph")
    }
    render() {
        return(
            <div className="bottom-tab">
                <button className="bottom-tab-btn left-btn" onClick={this.toBill}>账单</button>
                <button className="bottom-tab-btn" onClick={this.toGraph}>图表</button>
            </div>
        )
    }
 }

export default class BillList extends React.Component{
    constructor(props) {
        super(props);
    }

    componentDidMount() {
    }
    add(){
        history.push('add');
    }
    render() {
        const listItem = billList.map((oneDay, index) => {
            return (
                <div className="list-container" key={index}>
                    <div className="list-date">
                        <p>{oneDay.date}</p>
                    </div>
                    <ul>
                        {oneDay.list.map((item, index) => {
                            return (
                                <div className="list-item" key={index}>
                                    <div className="list-item-info">
                                        <p>{item.category}</p>
                                        <p>{item.remark}</p>
                                    </div>
                                    <div className="list-item-amount">
                                        <p><span>{item.type === 0 ? "+" : "-"}</span>{item.amount}</p>
                                    </div>
                                </div>
                            )
                        })}
                    </ul>
                </div>
            )
        });
        return(
            <div className="container">
                <div className="info-title">
                    <p>我的账单</p>
                </div>
                <div className="info-sum">
                    <div className="info-sum-time">
                        <p>
                            {sum.time}
                        </p>
                    </div>
                    <div className="info-sum-amount">
                        <p style={{marginRight:"10px"}}>
                           <span>总支出&yen;</span> {sum.out}
                        </p>
                        <p style={{marginRight:"10px"}}>
                            <span>总收入&yen;</span> {sum.in}
                        </p>
                    </div>
                </div>
                <div className="info-list">
                    <ul>{listItem}</ul>
                </div>
                <div >
                    <button className="add-btn" onClick={this.add}>记一笔</button>
                </div>
                <BottomTab />
            </div>
        )
    }
}
