import React from 'react'
import ReactECharts from 'echarts-for-react';
import { DatePicker } from 'antd-mobile';
import 'antd-mobile/dist/antd-mobile.css';
import {get} from "../../utils/request";
import history from "../../utils/history";
import "./Graph.css"

let optiont = {
    xAxis: {
        type: 'category',
        data: ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun']
    },
    yAxis: {
        type: 'value'
    },
    series: [{
        data: [,230, 224, 218, 135, 147, 2600],
        type: 'line'
    }]
};

export default class Graph extends React.Component{
    constructor(props) {
        super(props);
        this.list = [];
        this.state = {
            type: "1",
            date: new Date(),
        }
    }

    componentDidMount() {
        const data = {
            account: this.props.account,
            year: this.state.date.getFullYear(),
            month: this.state.date.getMonth() + 1,
        }
        console.log(data);
        get('getGraphDate', data).then((res) => {
            console.log(res.data.result.outDetail);
            this.setState({
                sumIn: res.data.result.sumIn,
                sumOut: res.data.result.sumOut,
                outDetail: res.data.result.outDetail,
                inDetail: res.data.result.inDetail,
                inList: res.data.result.inList,
                outList: res.data.result.outList,
            })
        }).catch((err) => {
            alert(err);
        })
    }
    changeType = (e) => {
        this.setState({[e.target.name]: e.target.value});
    }
    changeDate = (date) => {
        this.setState({date: date}, () => {this.componentDidMount()})
    }
    render() {
        this.list = this.state.type === "1" ? this.state.outList : this.state.inList;
        console.log(this.list);
        const optionPie = {
            tooltip: {
                trigger: "item"
            },
            series: [
                {
                    name: this.state.type === "1" ? "支出" : "收入",
                    type: "pie",
                    radius: "50%",
                    data: this.state.type === "1" ? this.state.outDetail : this.state.inDetail
                }
            ]
        };
        const optionList = {
            xAxis: {
                type: 'category',
                data: this.list.map((item) => {
                    return item.date;
                })
            },
            yAxis: {
                type: 'value'
            },
            series: [{
                data: this.list.map((item) => {
                    return item.amount;
                }),
                type: 'line'
            }]
        };
        return(
            <div className="graph-container">
                <div className="graph-title">
                    <p>图表分析</p>
                </div>
                <select name="type" className="select-item" onChange={this.changeType}>
                    <option value="1">支出</option>
                    <option value="0">收入</option>
                </select>
                <div>总计{this.state.type === "1" ? this.state.sumOut : this.state.sumIn}</div>
                <DatePicker
                    mode="month"
                    value={this.state.date}
                    onChange={this.changeDate}
                >
                    <div>{this.state.date.getFullYear()}-{this.state.date.getMonth() + 1}</div>
                </DatePicker>
                <ReactECharts option={optionPie} />
                <ReactECharts option={optiont} />
            </div>
        )
    }
}
